CKEDITOR.editorConfig = function( config ) {
    config.language='fa';
    config.skin='bootstrapck';
    config.removePlugins='about,a11yhelp,clipboard,find,flash,forms,iframe,magicline,pagebreak,pastefromword,preview,scayt,showblocks,smiley,specialchar,tabletools,templates,wsc';
    config.extraPlugins = 'wpmore';
    config.height='360px';
    config.width='100%';
};
